export default class Info extends Component {
    state = {
        first_name:'',
        last_name:'',
        email:'',
        password:'********'

    }
  handleEdit = () => {
    this.flag = false;
  };
  handleSave = () => {};
  onChangeHandler = (e) => {
    this.setState({
        [e.target.name]: e.target.value
    })
}
  flag = true;
  componentDidMount(){}        


  render() {
    return (
      <div>
        <label htmlFor="">First Name :</label>
        <input
          type="text"
          name="first_name"
          id=""
          value={this.flag==true? this.props.data.last_name : ''}

        />
        <label htmlFor="">Last Name :</label>
        <input
          type="text"
          name="last_name"
          id=""
          value={this.flag==true? this.props.data.last_name : ''}

        />
        <label htmlFor="">Email :</label>
        <input
          type="text"
          name="email"
          id=""
          value={this.state.email}

        />
        <label htmlFor="">Password :</label>
        <input
          type="text"
          name="password"
          id=""
          value="****************"
        />
        <div>
          <button onClick={this.handleEdit}>edit</button>
          <button>save</button>
        </div>
      </div>
    );
  }
}
