//
export default class Info extends Component {
    state = {
        first_name:this.props.data.first_name,
        last_name:this.props.data.last_name,
        email:this.props.data.email,
        password:'********'

    }
  handleEdit = () => {
    this.flag = false;
  };
  handleSave = () => {};
  flag = true;
  componentDidMount() {}

  render() {
    return (
      <div>
        <label htmlFor="">First Name :</label>
        <input
          type="text"
          name="ddd"
          id=""
          value={this.state.first_name}
        />
        <label htmlFor="">Last Name :</label>
        <input
          type="text"
          name="ddd"
          id=""
          value={this.flag==true? this.props.data.last_name : ''}
          disabled={this.flag}
        />
        <label htmlFor="">Email :</label>
        <input
          type="text"
          name="ddd"
          id=""
          value={this.flag==true? this.props.data.email : ''}
          disabled={this.flag}
        />
        <label htmlFor="">Password :</label>
        <input
          type="text"
          name="ddd"
          id=""
          value="****************"
          disabled={this.flag}
        />
        <div>
          <button onClick={this.handleEdit}>edit</button>
          <button>save</button>
        </div>
      </div>
    );
  }
}
