import React, { Component } from "react";
//
export default class Info extends Component {
  handleEdit = () => {
    this.flag = false;
  };
  handleSave = () => {};
  flag = true;
  componentDidMount() {}

  render() {
    return (
      <div>
        <label htmlFor="">First Name :</label>
        <input
          type="text"
          name="ddd"
          id=""
          value={this.flag==true? this.props.data.first_name : ''}
          disabled={this.flag}
        />
        <label htmlFor="">Last Name :</label>
        <input
          type="text"
          name="ddd"
          id=""
          value={this.flag==true? this.props.data.last_name : ''}
          disabled={this.flag}
        />
        <label htmlFor="">Email :</label>
        <input
          type="text"
          name="ddd"
          id=""
          value={this.props.data.email}
          disabled={this.flag}
        />
        <label htmlFor="">Password :</label>
        <input
          type="text"
          name="ddd"
          id=""
          value="****************"
          disabled={this.flag}
        />
        <div>
          <button onClick={this.handleEdit}>edit</button>
          <button>save</button>
        </div>
      </div>
    );
  }
}
