    }
  handleEdit = () => {
    this.flag = false;
  };
  handleSave = () => {};
  onChangeHandler = (e) => {
    this.setState({
        [e.target.name]: e.target.value,
        type: 1
    })
}
  flag = true;
  componentDidMount() {
      this.setState({
          first_name:this.props.data.first_name,
          last_name:this.props.data.last_name,
          email:this.props.data.email,
          password:'*************'
        }
          )
  }

  render() {
    return (
      <div>
        <label htmlFor="">First Name :</label>
        <input
          type="text"
          name="ddd"
          id=""
          value={this.flag==true? this.props.data.last_name : ''}
          disabled={this.flag}
        />
