        <input
          type="text"
          name="ddd"
          id=""
          value={this.flag==true? this.props.data.email : ''}
          disabled={this.flag}
        />
        <label htmlFor="">Password :</label>
        <input
          type="text"
          name="ddd"
          id=""
          value="****************"
          disabled={this.flag}
        />
        <div>
          <button onClick={this.handleEdit}>edit</button>
          <button>save</button>
        </div>
      </div>
    );
  }
}
